import React from 'react';  
import { motion } from 'framer-motion';  
import { Globe, Download, ExternalLink, AlertCircle, Scale, Settings } from 'lucide-react';  

const StandardsMegaCard = ({ title, description, whatIs, forWhat, link, logoIcon: Icon }) => {  
  const baseColor = 'from-green-500 via-emerald-600 to-teal-600';  
  const hoverColor = 'from-green-600 via-emerald-700 to-teal-700';  

  return (  
    <motion.div  
      className="bg-white/95 backdrop-blur-xl rounded-3xl p-8 shadow-2xl hover:shadow-3xl border border-white/50 overflow-hidden relative"  
      whileHover={{ y: -10, scale: 1.03 }}  
      initial={{ opacity: 0, y: 50 }}  
      animate={{ opacity: 1, y: 0 }}  
      transition={{ duration: 0.6 }}  
    >  
      <div className="absolute top-4 right-4 opacity-80">  
        <motion.div  
          className={`p-3 rounded-2xl bg-gradient-to-br ${baseColor} text-white`}  
          whileHover={{ scale: 1.1 }}  
        >  
          {Icon ? <Icon className="w-6 h-6" /> : <Globe className="w-6 h-6" />}  
        </motion.div>  
      </div>  
      <div className="relative z-10">  
        <h3 className="text-3xl font-bold text-gray-800 mb-4 flex items-center">  
          {title}  
          <ExternalLink className="w-6 h-6 ml-2 text-gray-400" />  
        </h3>  
        <p className="text-gray-600 text-lg mb-6 leading-relaxed">{description}</p>  
        <div className="space-y-6 mb-8">  
          <div className="bg-blue-50 p-6 rounded-2xl border-l-4 border-blue-500">  
            <div className="flex items-start mb-2">  
              <AlertCircle className="w-5 h-5 text-blue-500 mt-0.5 mr-3 flex-shrink-0" />  
              <strong className="text-xl text-blue-800">¿Qué es?</strong>  
            </div>  
            <p className="text-gray-700 text-base">{whatIs}</p>  
          </div>  
          <div className="bg-green-50 p-6 rounded-2xl border-l-4 border-green-500">  
            <div className="flex items-start mb-2">  
              <Scale className="w-5 h-5 text-green-500 mt-0.5 mr-3 flex-shrink-0" />  
              <strong className="text-xl text-green-800">¿Para qué sirve?</strong>  
            </div>  
            <p className="text-gray-700 text-base">{forWhat}</p>  
          </div>  
        </div>  
        <a  
          href={link}  
          target="_blank"  
          rel="noopener noreferrer"  
          className="inline-flex items-center justify-center w-full px-8 py-4 bg-gradient-to-r from-green-500 via-emerald-500 to-teal-500 text-white rounded-2xl font-bold text-lg hover:from-green-600 via-emerald-600 to-teal-600 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105"  
        >  
          <Download className="w-5 h-5 mr-3" />  
          Más Información y Recursos Oficiales  
        </a>  
        <p className="text-xs text-gray-500 mt-4 text-center">Sitio oficial de la organización internacional</p>  
      </div>  
    </motion.div>  
  );  
};  

export default StandardsMegaCard;
