import React, { useState } from 'react';  
import { motion } from 'framer-motion';  
import { Search, X } from 'lucide-react';  

const SearchBar = ({ onSearch }) => {  
  const [query, setQuery] = useState('');  
  const [isFocused, setIsFocused] = useState(false);  

  const handleSubmit = (e) => {  
    e.preventDefault();  
    onSearch(query);  
  };  

  const clearSearch = () => {  
    setQuery('');  
    onSearch('');  
  };  

  return (  
    <motion.form  
      onSubmit={handleSubmit}  
      className="relative max-w-2xl mx-auto mb-12"  
      initial={{ scale: 0.95, opacity: 0 }}  
      animate={{ scale: 1, opacity: 1 }}  
      transition={{ duration: 0.5 }}  
    >  
      <div className={`relative transition-all duration-300 ${isFocused ? 'shadow-2xl shadow-cyan-500/20 ring-2 ring-cyan-500/50' : 'shadow-lg'}`}>  
        <Search className={`absolute left-4 top-1/2 transform -translate-y-1/2 w-6 h-6 transition-colors ${isFocused ? 'text-cyan-400' : 'text-gray-400'}`} />  
        <input  
          type="text"  
          value={query}  
          onChange={(e) => setQuery(e.target.value)}  
          onFocus={() => setIsFocused(true)}  
          onBlur={() => setIsFocused(false)}  
          placeholder="Busca leyes, estándares o términos éticos..."  
          className="w-full pl-12 pr-12 py-4 bg-slate-900/50 backdrop-blur-xl border border-cyan-500/20 rounded-2xl focus:outline-none focus:border-cyan-500/50 text-white placeholder-gray-400 font-medium text-lg"  
        />  
        {query && (  
          <motion.button  
            type="button"  
            onClick={clearSearch}  
            className="absolute right-4 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white transition-colors"  
            whileHover={{ scale: 1.1 }}  
            whileTap={{ scale: 0.9 }}  
          >  
            <X className="w-5 h-5" />  
          </motion.button>  
        )}  
      </div>  
      <p className="text-center text-sm text-gray-400 mt-2">Presiona Enter para buscar o haz clic en X para limpiar.</p>  
    </motion.form>  
  );  
};  

export default SearchBar;
